def main():
    n, k = map(int, input().split())
    A = list(map(int, input().split()))

    B = []
    for i, a in enumerate(A):
        B.append((a, i+1))
    B.sort()
    #print(B)

    bit = [0]*(n+1)

    def bit_query(i, bit):
        res = 0
        while i > 0:
            res += bit[i]
            i -= i & (-i)
        return res

    def bit_update(i, x, bit, n):
        while i <= n:
            bit[i] += x
            i += i & (-i)

    def lower_bound(x, bit, n):
        s = 0
        pos = 0
        depth = n.bit_length()
        v = 1 << depth
        for i in range(depth, -1, -1):
            k = pos + v
            if k <= n and s + bit[k] < x:
                    s += bit[k]
                    pos += v
            v >>= 1
        return pos+1

    for i in range(1, n+1):
        bit_update(i, 1, bit, n)

    ans = [0]*n
    who = 0
    while bit_query(n, bit):
        while B:
            a, i = B.pop()
            if bit_query(i, bit)-bit_query(i-1, bit) == 0:
                continue
            else:
                break
        temp = []
        pos = bit_query(i, bit)
        temp.append(i)
        for d in range(1, k+1):
            j = pos-d
            if j == 0:
                break
            else:
                p = lower_bound(j, bit, n)
                temp.append(p)
        for d in range(1, k+1):
            j = pos+d
            if j > bit_query(n, bit):
                break
            else:
                p = lower_bound(j, bit, n)
                temp.append(p)
        #print(temp)
        for p in temp:
            bit_update(p, -1, bit, n)
            ans[p-1] = str(1+who)
        who = 1-who
    print(''.join(ans))

